/* Simplest Modal Window by Xavi Esteve (Creative Commons BY-SA) http://xaviesteve.com//?p=3054 */function mw_close(){$("#mw_win,#mw_mask,#mw_style").remove()}function mw_load(a){mw_mask="background:#333;height:100%;opacity:0.33;position:fixed;top:0;left:0;width:100%;z-index:1000;";mw_win="background: white;border-radius: 5px;box-shadow: 0 1px 2px #666;left: 33%;padding: 2%;position: fixed;top: 33%;width: 33%;z-index: 1001;";$("head").append('<style id="mw_style">#mw_mask{'+mw_mask+"}#mw_win{"+mw_win+"}</style>");$("body").append('<div id="mw_mask"></div><div id="mw_win">'+a+"</div>");$("#mw_mask,.mw_close").unbind("click").bind("click",function(){mw_close()})}
